import React, { useContext, useState } from 'react';
import { Card, Form, Row, Col, Button, Container } from 'react-bootstrap'
import { useHistory } from 'react-router-dom';
import GameTable from '../components/GameTable';
import { useInput } from '../components/Validation';
import { changeEmail, deleteUser } from '../http/userAPI';
import { Context } from "../index";
import { MAIN_ROUTE } from '../utils/consts';

const Account = () => {
  const history = useHistory()
  const { user } = useContext(Context)
  const email = useInput(user.email, { isEmpty: true, minLength: 5, isEmail: true })
  const [password, setPassword] = useState('')

  const changeData = async () => {
    try {
      let data = await changeEmail(user.email, email.value, password)

      user.setEmail(email.value)
      history.push(MAIN_ROUTE)
    } catch (e) {
      alert(e.response.data.message)
    }
  }

  const deleteData = async () => {
    try {
      let data = await deleteUser(user.email, password)

      user.setIsAuth(false)
      history.push(MAIN_ROUTE)
    } catch (e) {
      alert(e.response.data.message)
    }
  }
  return (
    <Container className="mt-5">
      <Card className="m-5 p-5">
        {/* TITLE */}
        <div className="d-flex justify-content-center">
          <h2 className="m-auto">Personal data</h2>
        </div>
        <Form className="mt-5">
          {/* EMAIL */}
          {/* <Form className="mt-5">
          <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
            <Form.Label column sm={2}>
              Email
            </Form.Label>
            <Col sm={10}>
              <Form.Control type="email" placeholder="Email"
                value={email.value}
                onChange={e => email.onChange(e)}
                onBlur={e => email.onBlur(e)} />
              {(email.isDirty && email.isEmpty) &&
                <div style={{ color: 'red' }}>Email cannot be empty</div>}
              {(email.isDirty && email.minLengthError) &&
                <div style={{ color: 'red' }}>Email cannot be less 5 symbols</div>}
              {(email.isDirty && email.emailError) &&
                <div style={{ color: 'red' }}>Incorrect email</div>}
            </Col>
          </Form.Group> */}

          <Form>
            <Row className="mb-3">
              <Form.Group as={Col} controlId="formGridEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control type="email" placeholder="Enter email" />
              </Form.Group>

              <Form.Group as={Col} controlId="formGridPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" />
              </Form.Group>
            </Row>

            <Form.Group className="mb-3" controlId="formGridAddress1">
              <Form.Label>Address</Form.Label>
              <Form.Control placeholder="1234 Main St" />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formGridAddress2">
              <Form.Label>Address 2</Form.Label>
              <Form.Control placeholder="Apartment, studio, or floor" />
            </Form.Group>

            <Row className="mb-3">
              <Form.Group as={Col} controlId="formGridCity">
                <Form.Label>City</Form.Label>
                <Form.Control />
              </Form.Group>

              <Form.Group as={Col} controlId="formGridState">
                <Form.Label>State</Form.Label>
                <Form.Select defaultValue="Choose...">
                  <option>Choose...</option>
                  <option>...</option>
                </Form.Select>
              </Form.Group>

              <Form.Group as={Col} controlId="formGridZip">
                <Form.Label>Zip</Form.Label>
                <Form.Control />
              </Form.Group>
            </Row>

            <Form.Group className="mb-3" id="formGridCheckbox">
              <Form.Check type="checkbox" label="Check me out" />
            </Form.Group>

            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>

          {/* PASSWORD */}
          <Form.Group as={Row} className="mb-3" controlId="formHorizontalPassword">
            <Form.Label column sm={2}>
              Password
            </Form.Label>
            <Col sm={10}>
              <Form.Control type="password" placeholder="Enter current password"
                value={password}
                onChange={e => setPassword(e.target.value)}
              />
            </Col>
          </Form.Group>


          <Form.Group as={Row} className="mb-5 m-5">
            <Col></Col>

            {/* BUTTON SAVE CHANGE */}
            <Col sm={5}>
              <Button variant={"outline-dark"}
                onClick={changeData}
                disabled={!email.inputValid}
                className='account_btn'>
                Save change</Button>
            </Col>

            {/* BUTTON DELETE USER */}
            <Col sm={5}>
              <Button variant={"outline-danger"}
                onClick={deleteData}
                style={{ float: 'right' }}
                className='account_btn'>
                Delete account</Button>
            </Col>
          </Form.Group>
        </Form>

        {/* GAME HISTORY TABLE */}
        <GameTable />

      </Card>
    </Container >
  );
}

export default Account;
