import React, { useContext, useState } from 'react';
import { Card, Form, Row, Col, Button, div } from 'react-bootstrap'
import { useHistory } from 'react-router-dom';
import GameTable from '../components/GameTable';
import { useInput } from '../components/Validation';
import { changeEmail, deleteUser } from '../http/userAPI';
import { Context } from "../index";
import { MAIN_ROUTE } from '../utils/consts';

const Account = () => {
  const history = useHistory()
  const { user } = useContext(Context)
  const email = useInput(user.email, { isEmpty: true, minLength: 5, isEmail: true })
  const [password, setPassword] = useState('')

  const changeData = async () => {
    try {
      let data = await changeEmail(user.email, email.value, password)

      user.setEmail(email.value)
      history.push(MAIN_ROUTE)
    } catch (e) {
      alert(e.response.data.message)
    }
  }

  const deleteData = async () => {
    try {
      let data = await deleteUser(user.email, password)

      user.setIsAuth(false)
      history.push(MAIN_ROUTE)
    } catch (e) {
      alert(e.response.data.message)
    }
  }
  return (
    <div className="mt-5">
      <Card className="m-5 p-5">
        {/* TITLE */}
        <div className="d-flex justify-content-center">
          <h2 className="m-auto">Personal data</h2>
        </div>
        {/* EMAIL */}
        {/* <Form className="mt-5">
          <Form.Group as={Row} className="mb-3" controlId="formHorizontalEmail">
            <Form.Label column sm={2}>
              Email
            </Form.Label>
            <Col sm={10}>
              <Form.Control type="email" placeholder="Email"
              value={email.value}
                onChange={e => email.onChange(e)}
                onBlur={e => email.onBlur(e)} />
              {(email.isDirty && email.isEmpty) &&
                <div style={{ color: 'red' }}>Email cannot be empty</div>}
              {(email.isDirty && email.minLengthError) &&
                <div style={{ color: 'red' }}>Email cannot be less 5 symbols</div>}
              {(email.isDirty && email.emailError) &&
                <div style={{ color: 'red' }}>Incorrect email</div>}
            </Col>
          </Form.Group> */}
<div className="flex flex-col md:flex-row">
        <div className="w-full md:w-1/2 flex-1 form-control pr-2">
          <label className="label">
            <span className="label-text">Email</span>
          </label>
          <input
            type="text"
            name="email"
            placeholder={"Email"}
            className="input input-bordered"
            value={email.value}
            onChange={e => email.onChange(e)}
          />
          <span className="text-sm text-red-500">

          </span>
        </div>
        <div className="w-full md:w-1/2 flex-1 form-control pr-2">
          <label className="label">
            <span className="label-text">Email</span>
          </label>
          <input
            type="text"
            name="email"
            placeholder={"Email"}
            className="input input-bordered"
            value={email.value}
            onChange={e => email.onChange(e)}
          />
          <span className="text-sm text-red-500">

          </span>
        </div>
        </div>
            <Form className="mt-5">
        {/* PASSWORD */}
        <Form.Group as={Row} className="mb-3" controlId="formHorizontalPassword">
          <Form.Label column sm={2}>
            Password
          </Form.Label>
          <Col sm={10}>
            <Form.Control type="password" placeholder="Enter current password"
              value={password}
              onChange={e => setPassword(e.target.value)}
            />
          </Col>
        </Form.Group>


        <Form.Group as={Row} className="mb-5 m-5">
          <Col></Col>

          {/* BUTTON SAVE CHANGE */}
          <Col sm={5}>
            <Button variant={"outline-dark"}
              onClick={changeData}
              disabled={!email.inputValid}
              className='account_btn'>
              Save change</Button>
          </Col>

          {/* BUTTON DELETE USER */}
          <Col sm={5}>
            <Button variant={"outline-danger"}
              onClick={deleteData}
              style={{ float: 'right' }}
              className='account_btn'>
              Delete account</Button>
          </Col>
        </Form.Group>
      </Form>

      {/* GAME HISTORY TABLE */}
      <GameTable />

    </Card>
    </div >
  );
}

export default Account;
