import React, { useContext, useState, useEffect } from 'react';
import { Button, Card, Container, Form } from 'react-bootstrap';
import { LOGIN_ROUTE, MAIN_ROUTE, REGISTRATION_ROUTE } from '../utils/consts';
import { NavLink, useLocation, useHistory} from "react-router-dom";
import { login, registration } from '../http/userAPI';
import {Context} from "../index";

const useValidation = (value, validations) => {
	const [isEmpty, setEmpty] = useState(true)
	const [minLengthError, setMinLengthError] = useState(false)

	useEffect( () => {
		for (const validation in validations) {
			switch (validation) {
				case 'minLength':
					value.length < validations[validation] ? setMinLengthError(true) : setMinLengthError(false)
					break;
				case 'isEmpty':
					value ? setEmpty(false) : setEmpty(true)
					break;
			}
		}
	}, [value])

	return {
		isEmpty,
		minLengthError,
	}
}
const useInput = (initialValue, validations) => {
	const [value, setValue] = useState(initialValue)
	const [isDirty, setDirty] = useState(false)
	const valid = useValidation(value, validations)

	const onChange = (e) => {
		setValue(e.target.value)
	}

	const onBlur = (e) => {
		setDirty(true)
	}

	return {
		value,
		onChange,
		onBlur,
		isDirty,
		...valid
	}
}


const Auth = () => {
	const { user } = useContext(Context)
	const location = useLocation()
	const history = useHistory()
	const isLogin = location.pathname === LOGIN_ROUTE
	// const [email, setEmail] = useState('')
	// const [password, setPassword] = useState('')

	const email = useInput('', {isEmpty: true, minLength: 3})
	const password = useInput('', {isEmpty: true, minLength: 5})

	const signIn = async () => {
		try {
			let data;
			if (isLogin) {
				data = await login(email.value, password.value);
			} else {

				data = await registration(email.value, password.value);
			}
			console.log(data)

			user.setUser(user)
			user.setIsAuth(true)
			history.push(MAIN_ROUTE)
		} catch (e) {
			alert(e.response.data.message)
		}
	}

	return (
		<Container
			className="d-flex justify-content-center align-items-center"
			style={{ height: window.innerHeight - 54 }}>
			<Card style={{ width: 600 }} className="p-5">
				<h2 className="m-auto">{isLogin ? "Sign in to the site" : "Registration"}</h2>

				<Form className="d-flex flex-column">
					<Form.Control
						className="mt-3"
						placeholder="Enter your email..."
						value={email.value}
						onChange={e => email.onChange(e)}
						onBlur={e => email.onBlur(e)}
					/>
					<Form.Control
						className="mt-3"
						type="password"
						id="inputPassword5"
						aria-describedby="passwordHelpBlock"
						placeholder="Enter your password..."
						value={password.value}
						onChange={e => password.onChange(e)}
						onBlur={e => password.onBlur(e)}
					/>
				</Form>

				<Button
					disabled={false}
					variant={"outline-success"}
					className="mt-3"
					onClick={signIn}>
					{isLogin ? "Sign in" : "Log up"}
				</Button>

				{isLogin ?
					<div className="mt-3">Don't have an account yet?
						<NavLink to={REGISTRATION_ROUTE}> Create an account.</NavLink>
					</div>
					:
					<div className="mt-3">Already have an account?
						<NavLink to={LOGIN_ROUTE}> Sign in</NavLink>
					</div>}
			</Card>

		</Container>
	);
}

export default Auth;
