import React, { useContext, useState } from 'react';
import {Form, Row, Col, Button, Container} from 'react-bootstrap'
import Table from 'react-bootstrap/Table'
import { getGamesByUser } from '../http/userAPI';
import {Context} from "../index";

const GameTable = () => {
	const { user } = useContext(Context)

	const updateGames = async () => {
		try {
			let data = await getGamesByUser(user.email)

			let games = []
			{data.data.map((game, i) =>
				games.push({winner: game.winner, time: game.time})
			)}
			user.setGames(games)
		} catch (e) {
			alert(e.response.data.message)
		}
	}

	return (
		<Container className='m-3 p-3'>
			<div className="d-flex justify-content-center">
				<h2 className="m-auto">Game history</h2>
			</div>
			<Table className='m-3'
				striped bordered hover size="sm">
				<thead>
					<tr>
						<th>#</th>
						<th>Result</th>
						<th>Time</th>
					</tr>
				</thead>
				<tbody>
					{user.games.map((game, i) =>
						<tr key={i}>
							<td>{i + 1}</td>
							<td>{game.winner ? "win" : "lose"}</td>
							<td>{game.time}</td>
						</tr>
					)}
				</tbody>
			</Table>

			<Container className="mb-5 m-5">
        <Col></Col>

        {/* BUTTON UPDATE TABLE */}
        <Col sm={5}>
          <Button variant={"outline-dark"}
					onClick={updateGames}>
              Update</Button>
        </Col>
				<Col></Col>
      </Container>
		</Container>
	);
}

export default GameTable;
