import {makeAutoObservable} from "mobx";

export default class UserStore {
    constructor(){
        this._isAuth = false
        this._user = {}
        this._email = {}
        makeAutoObservable(this)
    }

    setIsAuth(bool){
        this._isAuth = bool
    }

    setUser(user) {
        this._user = user
    }

    setEmail(data)
    {
        this._email = data
    }

    get email(){
        return this._email
    }

    get isAuth(){
        return this._isAuth
    }

    get user() {
        return this._user
    }
};
